mapper.update:
update shop_product
set publish_status=#{publishStatus}
where id=#{id}

update shop_product
set new_status=#{newStatus}
where id=#{id}

update shop_product
set recommend_status=#{recommendStatus}
where id=#{id}

mapper.select:
select id, name, pic, sub_title, price, sale, comment_num, stock, low_stock, point, album_pics, category_id, brand_id, brand_name, trans_fee, specs, publish_status, new_status, recommend_status, sort, promotion_price, create_time, update_time, create_by, update_by from shop_product where deleted = 0
and create_by = #{params.createBy}
and publish_status = #{params.publishStatus}
and new_status = #{params.newStatus}
and recommend_status = #{params.recommendStatus}
and brand_id = #{params.brandId}
and name like concat('%', #{params.name},'%')
order by sort limit #{index}, #{pageSize}

select count(*)
from shop_product
where deleted = 0


    and create_by = #{params.createBy}


    and publish_status = #{params.publishStatus}


    and new_status = #{params.newStatus}


    and recommend_status = #{params.recommendStatus}


    and brand_id = #{params.brandId}


    and name like concat('%', #{params.name},'%')

select id, name, price, sale, stock, low_stock, category_id, brand_id, specs from shop_product where deleted=0 and id in (
#{id}
)

select p.id, p.name, p.pic, p.sub_title, p.price, p.sale, p.comment_num, p.stock, p.low_stock, p.point, p.album_pics, p.category_id, p.brand_id, p.brand_name, p.trans_fee, p.specs, p.publish_status, p.new_status, p.recommend_status, p.sort, p.promotion_price, p.create_time, p.update_time, p.create_by, p.update_by from shop_product as p where p.deleted = 0 and not exists ( select 1 from shop_product_pack spp where p.id=spp.product_id )
and create_by = #{params.createBy}
and p.publish_status = #{params.publishStatus}
and p.new_status = #{params.newStatus}
and p.recommend_status = #{params.recommendStatus}
and p.brand_id = #{params.brandId}
and p.name like concat('%', #{params.name},'%')
order by p.sort limit #{index}, #{pageSize}

 select
count(*)
 from shop_product as p
 where p.deleted = 0
 and not exists (
 select  1 from shop_product_pack spp
 where p.id=spp.product_id
 )

     and create_by = #{params.createBy}


     and p.publish_status = #{params.publishStatus}


     and p.new_status = #{params.newStatus}


     and p.recommend_status = #{params.recommendStatus}


     and p.brand_id = #{params.brandId}


     and p.name like concat('%', #{params.name},'%')
